function checkBot(id, query) {
	$.ajax({
	  url: query,
	  dataType: 'json',
	  success: function(data) {
			if (data == "Y") {
				$("#bot"+id).text('Bot found');
			} else if (data == "!") {
				$("#bot"+id).text('Error');
			} else {
				$("#bot"+id).text('Not a bot');
			}
	  }
	});
}